import React, { useState, useEffect } from 'react';
import { BrowserRouter, Link, Route, Switch, useHistory, useLocation  } from 'react-router-dom';
import { AppBar, Typography, Toolbar, Avatar, Button } from '@material-ui/core';

import { useDispatch } from 'react-redux';
import decode from 'jwt-decode';

import Home from '../pages/home';
import Uploading from '../uploading/client/src/App';
import Auth from './Auth/Auth';
import useStyles from './styles';

const NavBar = () =>{
  const classes = useStyles();
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
  const history = useHistory();
  const dispatch = useDispatch();
  
  const logout = () => {
    dispatch({ type: 'LOGOUT' });
    history.push('/');
    setUser(null);
  };

  console.log(user);

  useEffect(() => {
    const token = user?.token;

    setUser(JSON.parse(localStorage.getItem('profile')));
  }, []);

  return (
    <BrowserRouter>
      <nav className="navbar">
        <div class="logo">SoundScape Mapper</div>
        <ul class="nav-links">
          <input type="checkbox" id="checkbox_toggle" />
          <label for="checkbox_toggle" class="hamburger">&#9776;</label>
          <div class="menu">
            <li><Link to="/">Home</Link></li>
            <li><Link to="/upload">Upload</Link></li>
            <li><Link to="/home">Contact Us</Link></li>
            <div >
              {user?.result ? (
                <div className={classes.profile}>
                  
                  <Typography className={classes.userName} variant="h9">{user?.result.name}</Typography>
                  <Button variant="contained" className={classes.logout} color="secondary" onClick={logout}>Logout</Button>
                </div>
              ) : (
                <Button component={Link} to="/auth" variant="contained" color="primary">Sign In</Button>
              )}
      </div>
          </div>
          
        </ul>
        
        
          
      </nav>

      <Switch>
        <Route path="/home"><Home /></Route>
        <Route path="/upload"><Uploading /></Route>
        <Route path="/auth"><Auth /></Route>
      </Switch>
    </BrowserRouter>   
  );
};

export default NavBar;
