const authReducer = (state = { authData: null }, action) => {
  switch (action.type) {
    case 'AUTH':
      console.log(action?.data);
      return state;
    case 'LOGOUT':
      localStorage.clear();

      return { ...state, authData: null, loading: false, errors: null };
    default:
      return state;
  }
};

export default authReducer;
